import React from 'react';
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import CssBaseline from '@material-ui/core/CssBaseline';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    riddleFirst: {
        borderBottom: `1px dotted black`,
    },
    riddleSecond: {
        borderBottom: `1px solid black`,
    },
    riddleHeader:{

        fontSize: '36px',
        textAlign: 'center',
    },
    riddleNumberOfCountries:{
        fontSize: '28px',
        textAlign: 'center',   
    }
}));

const Riddle = (props) => {
    const riddle = props;
    console.log(props)
    const classes = useStyles();

    return (
    <React.Fragment>
		<div className={classes.riddleFirst}>
            <Typography 
            className={classes.riddleHeader}>
                {riddle.answers.question}
            </Typography>
        </div>
        <div className={classes.riddleSecond}>
            <Typography 
            className={classes.riddleNumberOfCountries}>
            Number of Answers: {riddle.answers.answers.length}
            </Typography>
		</div>
    </React.Fragment>
    );
}

export default Riddle
