import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField'
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Results from './Results';
import Guess from './Guess';

const useStyles = makeStyles((theme) => ({
	suggestion: {

		cursor: 'pointer',
        borderLeft: '1px solid black',
        borderRight: '1px solid black',
        borderBottom: '1px solid black',
        borderTop: '1px solid black',
        background: 'white',
        '&:hover': {
            background: "gray",
        }
	},

    correct: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'baseline',
        fontSize: '24px',
        textAlign: 'center',
        marginBottom: theme.spacing(2),
        color: 'green'
    },

    incorrect: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'baseline',
        fontSize: '24px',
        textAlign: 'center',
        marginBottom: theme.spacing(2),
        color: 'red'
    }, 


}));

const Game = (props) => {
    const [results, setResults] = useState([])
	const [text, setText] = useState([])
    const [suggestions, setSuggestions] = useState([])
    const [history, setHistory] = useState([])
    const [boolError, setBoolError] = useState(false)
    const [helpertext, setHelpertext] = useState([])
	const { answers } = props;
    const { countries } = props;
    const classes = useStyles();
    const onSuggestHandler = (text)=>{
        // console.log(text)
        setText(text);
        setSuggestions([]);
    }
	const onChangeHandler = (text) => {
        // console.log('text')
        // console.log('history: ', history)
        let matches = []
        if (text.length > 0) {
            matches = countries.filter(country => {
            const regex = new RegExp(`${text}`, "gi");
            return country.name.match(regex)
        })
        }

		setSuggestions(matches.slice(0,5))
        setText(text)

	}

    const handleSubmit = (e) => {
        console.log(text)
        e.preventDefault();
        if (history.includes(text)){
            setBoolError(true)
            setHelpertext('Already Guessed')
            return
        }


        let countryValidation 
        countryValidation = countries.filter(country => {
            const regex = new RegExp(`^${text}$`, "i");
            return (country.name.match(regex))
        })

        if (countryValidation==0){
            setBoolError(true)
            setHelpertext('Enter a valid country')
            setText('')
            return
        }

        history.push(text)

        for (var i = 0; i < answers.answers.length; i++){
            if (answers.answers[i].name == text){
                results.push(true)
                setText('')
                return
            }
        }

        // console.log('answers', answers)
        // console.log('guess: ', text)

        results.push(false)
        // console.log('results', results)

        setText('')

    }

	if (!answers || answers.length === 0) return <p>Can not find any answers, sorry</p>;
	return (
		<React.Fragment>
            <Results results = {results} history = {history}/>
            <Guess suggestions = {suggestions}/>
		</React.Fragment>
	);
};

export default Game;
