import React, { useEffect, useState } from 'react';
import './App.css';
import Answers from './components/Answers';
import AnswersLoadingComponent from './components/AnswersLoading';
import RiddleLoadingComponent from './components/RiddleLoading';
import axios from 'axios'
import { StylesContext } from '@material-ui/styles';
import Guess from './components/Guess';
import Riddle from './components/Riddle';

function App() {
	const AnswersLoading = AnswersLoadingComponent(Guess);
	const RiddleLoading = RiddleLoadingComponent(Riddle)
	const [appState, setAppState] = useState({
		loading: false,
		countries: null,
		answers: null
	});

	useEffect(() => {
		setAppState({ loading: true });
		//this is where you should load the answers or probably just get rid of it and use a custom hook

		const loadAnswers = async () => {
			const answerData = await axios.get('http://127.0.0.1:8000/api/riddles/1')
			setAppState({ loadingAnswers: false, answers: answerData.data, countries: countryData.data});
		}
		loadAnswers();

		const loadCountries = async () => {
			const countryData = await axios.get('http://127.0.0.1:8000/api/countries/')
			setAppState({ loading: false, answers: answerData.data, countries: countryData.data});
		}
		loadAnswers();

	},[setAppState]);


	return (
		<div className="App">
			<RiddleLoading isLoading={appState.loading} answers={appState.answers}/>
			<AnswersLoading isLoading={appState.loading} answers={appState.answers} countries={appState.countries}/>
		</div>

	);
}
export default App;
