import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField'
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
	TextField: {
		paddingTop: '56.25%', // 16:9
	}
}));

const Guess = (props) => {
	const [text, setText] = useState([]);
    const [suggestions, setSuggestions] = useState([])
	const { countries } = props;
	const onChangeHandler = (text) => {
        let matches = []
        if (text.length > 0) {
            matches = countries.filter(country => {
            const regex = new RegExp(`${text}`, "gi");
            return country.name.match(regex)
        })
        }
        console.log('matches', matches)
		setSuggestions(matches)
        setText(text)

	}


	if (!countries || countries.length === 0) return <p>Can not find any countries, sorry</p>;
	return (
		<React.Fragment>

			<Container maxWidth="md" component="main">
                <Card className="guess card">

                <CardContent className="guess">
                    <Typography
                        gutterBottom
                        variant="h6"
                        component="h2"
                        className={classes.countryName}
                    >
                        Guess
                    </Typography>
                    <div className={classes.countryCapital}>
                        <Typography
                            component="p"
                            color="textPrimary"
                        ></Typography>
                        <Typography variant="subtitle1" color="textSecondary">
                            {country.capital}
                        </Typography>
                    </div>
                </CardContent>
                </Card>

                {suggestions && suggestions.map((suggestion,i) =>
                    <Typography  key={i} variant="h6" color="inherit" noWrap>
                        {suggestion.name}
                    </Typography>
                )}
            <TextField
                id="outlined-guess"
                label="Guess"
                onChange={e=> onChangeHandler(e.target.value)}
                value={text}
            />

            </Container>
		</React.Fragment>
	);
};
export default Guess;
