import React, { useEffect, useState } from 'react';
import './App.css';
import Countries from './components/Countries';
import CountryLoadingComponent from './components/CountryLoading';
import axios from 'axios'

function App() {
	const CountryLoading = CountryLoadingComponent(Countries);
	const [appState, setAppState] = useState({
		loading: false,
		countries: null,
	});

	useEffect(() => {
		setAppState({ loading: true });
		//this is where you should load the answers or probably just get rid of it and use a custom hook
		const apiUrl = `http://127.0.0.1:8000/api/`;

		const loadCountries = async () => {
			const response = await axios.get('http://127.0.0.1:8000/api/')
			console.log(response)
			//setAppState({ loading: false, countries: countries });

		}
		loadCountries();

		fetch(apiUrl)
			.then((data) => data.json())
			.then((countries) => {
				setAppState({ loading: false, countries: countries });
			});
	}, [setAppState]);
	return (
		<div className="App">
			<h1>Correct Countries</h1>
			<CountryLoading isLoading={appState.loading} countries={appState.countries} />
		</div>
	);
}
export default App;
