import React, { useState } from 'react';
import Typography from '@material-ui/core/Typography';
import { Container } from '@material-ui/core';
import {Card} from '@material-ui/core';
import {CardContent} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';



const Riddle = (props) => {

    const {answers, guesses, classes, strikes, riddleHeaderClass} = props;

    return (
    <React.Fragment>
        <Container maxWidth="md"  >
            <Card className="resultCard">
                <CardContent className="result">
                    <div className={classes.riddleFirst}>
                        <Typography 
                        className={classes.riddleHeader}>
                            GeoRiddle #{answers.id}: {answers.question}
                        </Typography>
                    </div>
                    <div className={classes.riddleSecond}>
                        <Typography 
                        className={riddleHeaderClass}>
                            Remaining: {answers.answers.length + strikes.length - guesses.length } Countries || {3-strikes.length} Strikes
                        </Typography>
                    </div>
                </CardContent>
            </Card>
        </Container>
    </React.Fragment>
    );
}

export default Riddle
